meta.custom_css:
.mute,
.longform .menu_open {
  border-radius: 0;
  bacground-color: rgb(19,19,22);
}

table {
  border: 1px solid rgb(135,178,121);
  border-collapse: collapse;
  margin: 0;
  padding: 0;
  width: 100%;
  table-layout: fixed;
}

table caption {
  font-family: 'Press Start 2P', cursive;
  font-size: 0.72em;
  color: #9c0000;
  margin: .5em 0 .72em;
}

table tr {
  border: 1px solid rgb(135,178,121);
  padding: .35em;
}

table tr:nth-child(even) {
  background-color: rgba(0,0,0,0.1);
}

table th,
table td {
  padding: .625em;
  text-align: left;
}

table th {
  font-size: .85em;
  letter-spacing: .1em;
  text-transform: uppercase;
  text-align: center;
  background-color: rgb(135,178,121);
}

@media screen and (max-width: 599px) {
  table {
    border: 0;
  }

  table thead {
    border: none;
    clip: rect(0 0 0 0);
    height: 1px;
    margin: -1px;
    overflow: hidden;
    padding: 0;
    position: absolute;
    width: 1px;
  }
  
  table tr {
    border-bottom: 3px solid rgb(135,178,121);
    display: block;
    margin-bottom: .625em;
  }
  
  table td {
    border-bottom: 1px solid rgb(135,178,121);
    display: block;
    font-size: .8em;
    text-align: right;
  }
  
  table td::before {
    content: attr(data-label);
    float: left;
    font-weight: bold;
    text-transform: uppercase;
  }
  
  table td:last-child {
    border-bottom: 0;
  }
}

article .header-fullpage h1,
article .header h1 {
    font-size: 1.5em;
    line-height: 1.8em;
    color: #630000;
}

article .header-fullpage h2,
article .header h2 {
    font-size: 1em;
    line-height: 1.5em;
    color: #9c0000;
}

@media (min-width: 475px) {
article .header-fullpage h1,
article .header h1 {
    font-size: 2.2em;
    line-height: 1.4em;
}

article .header-fullpage h2,
article .header h2 {
    font-size: 1.5em;
    line-height: 1.2em;
}
}

article .part.reverse_colors {
   background: #bbd6ef;
   color: #000
}
